"use strict";(globalThis.webpackChunkveto_docs=globalThis.webpackChunkveto_docs||[]).push([[1567],{5226(e){e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Protocol Introduction","items":[{"type":"link","href":"/intro","label":"Introduction","docId":"intro","unlisted":false},{"type":"link","href":"/architecture","label":"Architecture & Protocol Design","docId":"architecture","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"The Safety Stack","items":[{"type":"link","href":"/features/identity","label":"Cryptographic Identity Attestation","docId":"features/identity","unlisted":false},{"type":"link","href":"/features/risk","label":"Institutional Risk & Compliance Engine","docId":"features/risk","unlisted":false},{"type":"link","href":"/features/handshake","label":"Bilateral Consensus Handshake","docId":"features/handshake","unlisted":false},{"type":"link","href":"/features/shield","label":"Document Integrity Shield","docId":"features/shield","unlisted":false},{"type":"link","href":"/features/monitoring","label":"Continuous Infrastructure Integrity","docId":"features/monitoring","unlisted":false}],"collapsed":true,"collapsible":true}]},"docs":{"api-reference":{"id":"api-reference","title":"Veto API Reference","description":"Welcome to the Veto API reference. This document outlines the primary endpoints and cryptographic payloads used for institutional B2B stablecoin settlement integrity."},"architecture":{"id":"architecture","title":"Architecture & Protocol Design","description":"Veto is architected as a High-Availability Integrity Layer that sits between the business logic and the blockchain execution layer. Our design follows a Shadow-Security model, where every transaction is validated in a parallel, non-blocking environment before being cleared for settlement.","sidebar":"tutorialSidebar"},"features/handshake":{"id":"features/handshake","title":"Bilateral Consensus Handshake","description":"The Bilateral Consensus Handshake is the cornerstone of the Veto Safety Stack. It introduces a critical layer of Pre-Settlement Finality, ensuring that high-value stablecoin transfers are only executed once both participants have cryptographically validated the intent, amount, and destination.","sidebar":"tutorialSidebar"},"features/identity":{"id":"features/identity","title":"Cryptographic Identity Attestation","description":"The Veto Identity Protocol establishes a verifiable Chain of Trust between traditional corporate assets (Domains) and decentralized financial identifiers (Wallets). This mechanism eliminates the anonymity risk inherent in public ledger transactions.","sidebar":"tutorialSidebar"},"features/monitoring":{"id":"features/monitoring","title":"Continuous Infrastructure Integrity","description":"In the digital asset space, identity is not a static attribute but a persistent state of integrity. Veto\u2019s Continuous Integrity Monitoring ensures that once a counterparty is verified, their infrastructure remains untampered and secure throughout the entire lifecycle of the business relationship.","sidebar":"tutorialSidebar"},"features/risk":{"id":"features/risk","title":"Institutional Risk & Compliance Engine","description":"Veto provides a sophisticated Institutional Risk Engine that serves as an autonomous gatekeeper for every stablecoin transaction. By moving beyond reactive monitoring to proactive Pre-Flight Screening, organizations can maintain absolute compliance and operational integrity.","sidebar":"tutorialSidebar"},"features/shield":{"id":"features/shield","title":"Document Integrity Shield","description":"Business Email Compromise (BEC) and invoice hijacking remain the primary vectors for large-scale B2B financial loss. Attackers frequently compromise vendor communication channels to substitute legitimate payment instructions with malicious wallet addresses. Document Integrity Shield is Veto\'s automated defense against these sophisticated impersonation attacks.","sidebar":"tutorialSidebar"},"intro":{"id":"intro","title":"Introduction","description":"Veto is the first institutional-grade Identity & Safety Layer purpose-built for the B2B stablecoin economy.","sidebar":"tutorialSidebar"}}}}')}}]);